sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

  data.incident = [];

	var inc_record = new GlideRecord('incident');
	inc_record.addActiveQuery('state=1');
	inc_record.query();

	while(inc_record.next())
		{
			var inc = {};
			inc.sys = inc_record.getValue('sys_id');
			inc.number = inc_record.getValue('number');
			inc.caller = inc_record.getDisplayValue('caller_id');
			inc.sd = inc_record.getValue('short_description');

			data.incident.push(inc);
		}



})();
